@RestController
@RequiredArgsConstructor
@RequestMapping("/api/file")
public class FileResourceController {
    // ... members ...
}

// ... code ...

private final FileResourceService fileResourceService;
private final LogService log;
@PostMapping(value = "/upload", consumes = MediaType.MULTIPART_FORM_DATA_VALUE)
    public ResponseEntity<UUID> uploadModel(@RequestParam("file") MultipartFile file) {
        try {
            log.info("FileResourceController", "Uploading file: " + file.getOriginalFilename());
            return ResponseEntity.ok(fileResourceService.upload(file, PrinterCostants.PRINTER_MODEL_STORAGE_BUCKET_NAME).getId());
        } catch (Exception e) {
            log.error("FileResourceController", "Upload failed: " + e.getMessage());
            throw new RuntimeException("Upload failed", e);
        }
    }
@PostMapping(value = "/upload/image", consumes = MediaType.MULTIPART_FORM_DATA_VALUE)
    public ResponseEntity<UUID> uploadImage(@RequestParam("file") MultipartFile file) {
        try {
            log.info("FileResourceController", "Uploading image file: " + file.getOriginalFilename());
            return ResponseEntity.ok(fileResourceService.upload(file, PrinterCostants.PRINTER_IMAGE_STORAGE_BUCKET_NAME).getId());
        } catch (Exception e) {
            log.error("FileResourceController", "Upload image failed: " + e.getMessage());
            throw new RuntimeException("Upload failed", e);
        }
    }
@GetMapping("/download")
    public ResponseEntity<StreamingResponseBody> download(@RequestParam("id") @NotNull UUID id) {
        log.info("FileResourceController", "Download requested for file ID: " + id);
        return _download(fileResourceService.download(id), id);
    }
@GetMapping("/download/glb")
    public ResponseEntity<StreamingResponseBody> downloadGlb(@RequestParam("id") @NotNull UUID id) {
        log.info("FileResourceController", "GLB Download requested for file ID: " + id);
        return _download(fileResourceService.downloadGlb(id), id);
    }
@GetMapping("/download/ensure")
    public ResponseEntity<String> ensureDownload(@RequestParam("driverId") @NotNull UUID driverId, @RequestParam("resourceId") @NotNull UUID resourceId) {
        log.info("FileResourceController", "Ensuring download resource for file ID: " + resourceId + " and driver ID: " + driverId);
        return ResponseEntity.ok(this.fileResourceService.ensureResource(resourceId, driverId));
    }
private ResponseEntity<StreamingResponseBody> _download(InputStream inputStream, UUID id) {
        StreamingResponseBody responseBody = outputStream -> {
            try (inputStream) {
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = inputStream.read(buffer)) != -1) {
                    outputStream.write(buffer, 0, bytesRead);
                }
                outputStream.flush();
            } catch (IOException e) {
                log.error("FileResourceController", "Error streaming file with ID: " + id);
                throw new RuntimeException("Error streaming file", e);
            }
        };
        return ResponseEntity.ok()
                .header(HttpHeaders.CONTENT_DISPOSITION, "attachment; filename=\"" + id + "\"")
                .contentType(MediaType.APPLICATION_OCTET_STREAM)
                .body(responseBody);
    }

