        return modelRepository.findAll()
                .stream()
                .map(modelMapper::toDto)
                .toList();
    }
@Override
    public ModelDto getModelById(UUID id) {
        return modelRepository.findById(id)
                .map(modelMapper::toDto)
                .orElseThrow(() -> new RuntimeException("Model not found with id: " + id));
    }
