@Autowired
    private SlicingPropertyMapper slicingPropertyMapper;
@Override
    public SlicingResultDto toDto(SlicingResult result) {
        if ( result == null ) {
            return null;
        }
        SlicingResultDto.SlicingResultDtoBuilder slicingResultDto = SlicingResultDto.builder();
        slicingResultDto.sourceId( resultSourceFileId( result ) );
        slicingResultDto.generatedId( resultGeneratedFileId( result ) );
        slicingResultDto.slicingProperty( slicingPropertyMapper.toDto( result.getSlicingProperty() ) );
        slicingResultDto.id( result.getId() );
        slicingResultDto.lines( result.getLines() );
        slicingResultDto.createdAt( result.getCreatedAt() );
        return slicingResultDto.build();
    }
@Override
    public List<SlicingResultDto> toDto(List<SlicingResult> results) {
        if ( results == null ) {
            return null;
        }
        List<SlicingResultDto> list = new ArrayList<SlicingResultDto>( results.size() );
        for ( SlicingResult slicingResult : results ) {
            list.add( toDto( slicingResult ) );
        }
        return list;
    }
private UUID resultSourceFileId(SlicingResult slicingResult) {
        if ( slicingResult == null ) {
            return null;
        }
        FileResource sourceFile = slicingResult.getSourceFile();
        if ( sourceFile == null ) {
            return null;
        }
        UUID id = sourceFile.getId();
        if ( id == null ) {
            return null;
        }
        return id;
    }
private UUID resultGeneratedFileId(SlicingResult slicingResult) {
        if ( slicingResult == null ) {
            return null;
        }
        FileResource generatedFile = slicingResult.getGeneratedFile();
        if ( generatedFile == null ) {
            return null;
        }
        UUID id = generatedFile.getId();
        if ( id == null ) {
            return null;
        }
        return id;
    }

